package br.pucrio.inf.learn.structlearning.discriminative.application.dpgs;

/**
 * Grandparent/siblings application exception.
 * 
 * @author eraldo
 * 
 */
public class DPGSException extends Exception {

	/**
	 * Automatically generated serial version ID.
	 */
	private static final long serialVersionUID = 7394200936480926522L;

	public DPGSException() {
	}

	public DPGSException(String message) {
		super(message);
	}

	public DPGSException(Throwable cause) {
		super(cause);
	}

	public DPGSException(String message, Throwable cause) {
		super(message, cause);
	}

	public DPGSException(String message, Throwable cause,
			boolean enableSuppression, boolean writableStackTrace) {
		super(message, cause, enableSuppression, writableStackTrace);
	}

}
